import React, { useState } from "react";
import { ChevronRight, Terminal } from "lucide-react";

const ShellOperationEvent = ({ payload, eventType }) => {
  const [isExpanded, setIsExpanded] = useState(false);

  if (!payload) return null;

  const getEventDetails = (eventType) => {
    switch (eventType) {
      case "shell_exec":
        return {
          icon: (
            <Terminal className="w-3 h-3 text-purple-600 group-hover:text-purple-700 transition-colors" />
          ),
          action: "Executing command",
          bgClass: "bg-purple-50 hover:bg-purple-100 border-purple-200",
        };
      case "shell_view":
        return {
          icon: (
            <Terminal className="w-3 h-3 text-blue-600 group-hover:text-blue-700 transition-colors" />
          ),
          action: "Managing process",
          bgClass: "bg-blue-50 hover:bg-blue-100 border-blue-200",
        };
      case "shell_write":
        return {
          icon: (
            <Terminal className="w-3 h-3 text-yellow-600 group-hover:text-yellow-700 transition-colors" />
          ),
          action: "Writing to shell",
          bgClass: "bg-yellow-50 hover:bg-yellow-100 border-yellow-200",
        };
      default:
        return {
          icon: (
            <Terminal className="w-3 h-3 text-purple-600 group-hover:text-purple-700 transition-colors" />
          ),
          action: "Shell operation",
          bgClass: "bg-purple-50 hover:bg-purple-100 border-purple-200",
        };
    }
  };

  const eventDetails = getEventDetails(eventType);

  const toggleExpanded = () => {
    setIsExpanded(!isExpanded);
  };

  const getDisplayContent = () => {
    if (eventType === "shell_view") {
      const processId = payload.id || "Unknown process";
      const action = payload.kill_process ? "Terminating" : "Viewing";
      return `${action} ${processId}`;
    } else if (eventType === "shell_write") {
      const sessionId = payload.id || "Unknown session";
      const input = payload.input || "";
      const truncateInput = (input, maxLength = 40) => {
        return input.length > maxLength
          ? `${input.substring(0, maxLength)}...`
          : input;
      };
      return `Writing to ${sessionId}: ${truncateInput(input)}`;
    } else {
      const command = payload.command || "Unknown command";
      const truncateCommand = (cmd, maxLength = 50) => {
        return cmd.length > maxLength
          ? `${cmd.substring(0, maxLength)}...`
          : cmd;
      };
      return truncateCommand(command);
    }
  };

  const renderExpandedContent = () => {
    if (eventType === "shell_view") {
      return (
        <div className="mx-3 mb-4 bg-white border border-blue-200 rounded-xl shadow-sm overflow-hidden">
          <div className="flex items-center px-4 py-3 bg-blue-50 border-b border-blue-200">
            <Terminal className="w-4 h-4 mr-2 text-blue-600" />
            <span className="text-sm font-mono text-blue-700">
              Process Management
            </span>
          </div>
          <div className="p-4 font-mono text-sm">
            <div className="space-y-3">
              <div className="flex items-center">
                <span className="text-blue-600 mr-2 font-medium">
                  Process ID:
                </span>
                <span className="text-gray-700">{payload.id || "N/A"}</span>
              </div>
              <div className="flex items-center">
                <span className="text-blue-600 mr-2 font-medium">Action:</span>
                <span
                  className={`${
                    payload.kill_process ? "text-red-600" : "text-green-600"
                  } font-medium`}
                >
                  {payload.kill_process ? "Kill Process" : "View Process"}
                </span>
              </div>
              {payload.wait_seconds !== undefined && (
                <div className="flex items-center">
                  <span className="text-blue-600 mr-2 font-medium">
                    Wait Time:
                  </span>
                  <span className="text-gray-700">
                    {payload.wait_seconds
                      ? `${payload.wait_seconds} seconds`
                      : "No wait"}
                  </span>
                </div>
              )}
              {payload.output && (
                <div className="mt-4">
                  <div className="text-blue-600 mb-2 font-medium">Output:</div>
                  <div className="text-gray-700 whitespace-pre-wrap break-words bg-gray-50 p-3 rounded-lg border">
                    {String(payload.output)}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      );
    } else if (eventType === "shell_write") {
      return (
        <div className="mx-3 mb-4 bg-white border border-yellow-200 rounded-xl shadow-sm overflow-hidden">
          <div className="flex items-center px-4 py-3 bg-yellow-50 border-b border-yellow-200">
            <Terminal className="w-4 h-4 mr-2 text-yellow-600" />
            <span className="text-sm font-mono text-yellow-700">
              Shell Input
            </span>
          </div>
          <div className="p-4 font-mono text-sm">
            <div className="space-y-3">
              <div className="flex items-center">
                <span className="text-yellow-600 mr-2 font-medium">
                  Session ID:
                </span>
                <span className="text-gray-700">{payload.id || "N/A"}</span>
              </div>
              <div className="flex items-center">
                <span className="text-yellow-600 mr-2 font-medium">
                  Press Enter:
                </span>
                <span
                  className={`${
                    payload.press_enter ? "text-green-600" : "text-gray-600"
                  } font-medium`}
                >
                  {payload.press_enter ? "Yes" : "No"}
                </span>
              </div>
              {payload.input && (
                <div className="mt-4">
                  <div className="text-yellow-600 mb-2 font-medium">Input:</div>
                  <div className="bg-gray-900 text-white p-3 rounded-lg font-mono text-xs">
                    <div className="flex items-start">
                      <span className="text-green-400 mr-2">{">"}</span>
                      <span className="text-white whitespace-pre-wrap break-words">
                        {payload.input}
                        {payload.press_enter && (
                          <span className="text-green-400"> ⏎</span>
                        )}
                      </span>
                    </div>
                  </div>
                </div>
              )}
              {payload.output && (
                <div className="mt-4">
                  <div className="text-yellow-600 mb-2 font-medium">
                    Output:
                  </div>
                  <div className="text-gray-700 whitespace-pre-wrap break-words bg-gray-50 p-3 rounded-lg border">
                    {String(payload.output)}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      );
    } else {
      // Original terminal view for shell_exec
      const command = payload.command || "Unknown command";
      return (
        <div className="mx-3 mb-4 bg-gray-900 text-white rounded-xl shadow-sm overflow-hidden">
          <div className="flex items-center px-4 py-3 bg-gray-800 border-b border-gray-700">
            <Terminal className="w-4 h-4 mr-2 text-green-400" />
            <span className="text-sm font-mono text-green-400">terminal</span>
          </div>
          <div className="p-4 font-mono text-sm">
            <div className="flex items-center mb-3">
              <span className="text-green-400 mr-2">$</span>
              <span className="text-white">{command}</span>
            </div>
            {payload.output && (
              <div className="text-gray-300 whitespace-pre-wrap break-words">
                {String(payload.output)}
              </div>
            )}
          </div>
        </div>
      );
    }
  };

  return (
    <>
      <div className="flex justify-start">
        <div
          className={`group flex items-center space-x-2 px-3 py-2 ${eventDetails.bgClass} border rounded-lg transition-all duration-200 hover:shadow-sm`}
        >
          {eventDetails.icon}
          <span className="text-xs text-gray-500 truncate max-w-md">
            {eventDetails.action} <strong>{getDisplayContent()}</strong>
          </span>
          <button
            onClick={toggleExpanded}
            className="flex items-center py-1 px-2 text-xs text-gray-400 hover:text-gray-600 hover:bg-white rounded transition-all duration-200 opacity-0 group-hover:opacity-100"
            title={isExpanded ? "Hide details" : "Show details"}
          >
            <div
              className={`transition-transform duration-200 ${
                isExpanded ? "rotate-90" : "rotate-0"
              }`}
            >
              <ChevronRight className="w-3 h-3" />
            </div>
          </button>
        </div>
      </div>

      <div
        className={`grid transition-all duration-300 ease-in-out ${
          isExpanded
            ? "grid-rows-[1fr] opacity-100"
            : "grid-rows-[0fr] opacity-0"
        }`}
      >
        <div className="overflow-hidden">{renderExpandedContent()}</div>
      </div>
    </>
  );
};

export default ShellOperationEvent;
