import React from "react";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";

// Function to detect and convert plain URLs to links
const linkifyText = (text) => {
  if (typeof text !== "string") return text;

  // Comprehensive URL regex pattern
  const urlRegex = /(https?:\/\/[^\s<>"'\]]+|www\.[^\s<>"'\]]+)/gi;

  const parts = [];
  let lastIndex = 0;
  let match;

  while ((match = urlRegex.exec(text)) !== null) {
    // Add text before the URL
    if (match.index > lastIndex) {
      parts.push(text.slice(lastIndex, match.index));
    }

    // Add the URL as a clickable link
    const url = match[0];
    const href = url.startsWith("http") ? url : `https://${url}`;
    parts.push(
      <a
        key={match.index}
        href={href}
        target="_blank"
        rel="noopener noreferrer"
        className="text-blue-600 hover:underline"
      >
        {url}
      </a>
    );

    lastIndex = match.index + match[0].length;
  }

  // Add remaining text after the last URL
  if (lastIndex < text.length) {
    parts.push(text.slice(lastIndex));
  }

  return parts.length > 1 ? parts : text;
};

// Helper function to process children and linkify text
const processChildren = (children) => {
  if (typeof children === "string") {
    return linkifyText(children);
  }
  if (Array.isArray(children)) {
    return children.map((child, index) => {
      if (typeof child === "string") {
        return (
          <React.Fragment key={index}>{linkifyText(child)}</React.Fragment>
        );
      }
      return child;
    });
  }
  return children;
};

// Reusable markdown renderer component
const MarkdownRenderer = ({ children, className = "" }) => (
  <div
    className={`text-sm rounded prose prose-sm prose-gray max-w-none ${className}`}
  >
    <ReactMarkdown
      remarkPlugins={[remarkGfm]}
      components={{
        // Custom text component to handle URL auto-linking
        text: ({ children }) => {
          return linkifyText(children);
        },
        p: ({ children }) => (
          <p className="mb-2 last:mb-0">{processChildren(children)}</p>
        ),
        h1: ({ children }) => (
          <h1 className="text-lg font-bold mb-2">
            {processChildren(children)}
          </h1>
        ),
        h2: ({ children }) => (
          <h2 className="text-base font-bold mb-2">
            {processChildren(children)}
          </h2>
        ),
        h3: ({ children }) => (
          <h3 className="text-sm font-bold mb-1">
            {processChildren(children)}
          </h3>
        ),
        code: ({ children }) => (
          <code className="bg-gray-200 px-1 py-0.5 rounded text-xs font-mono">
            {children}
          </code>
        ),
        pre: ({ children }) => (
          <pre className="bg-gray-200 p-2 rounded text-xs overflow-x-auto">
            {children}
          </pre>
        ),
        ul: ({ children }) => (
          <ul className="list-disc list-inside mb-2">{children}</ul>
        ),
        ol: ({ children }) => (
          <ol className="list-decimal list-inside mb-2">{children}</ol>
        ),
        li: ({ children }) => (
          <li className="mb-1">{processChildren(children)}</li>
        ),
        blockquote: ({ children }) => (
          <blockquote className="border-l-4 border-gray-300 pl-3 italic">
            {processChildren(children)}
          </blockquote>
        ),
        // Handle existing markdown links normally (don't double-process them)
        a: ({ href, children }) => (
          <a
            href={href}
            target="_blank"
            rel="noopener noreferrer"
            className="text-blue-600 hover:underline"
          >
            {children}
          </a>
        ),
      }}
    >
      {children}
    </ReactMarkdown>
  </div>
);

export default MarkdownRenderer;
