import React, { useState } from "react";
import { Activity, ChevronRight, Eye } from "lucide-react";

const ImageGenerationEvent = ({ payload, onPreviewClick }) => {
  const [isExpanded, setIsExpanded] = useState(false);

  if (!payload) return null;

  const filename = payload.filename || "generated_image.png";

  const toggleExpanded = () => {
    setIsExpanded(!isExpanded);
  };

  const truncateFilename = (filename, maxLength = 50) => {
    if (!filename) return "Unknown file";
    return filename.length > maxLength
      ? `${filename.substring(0, maxLength)}...`
      : filename;
  };

  const handlePreviewClick = () => {
    if (payload.url && onPreviewClick) {
      onPreviewClick({
        url: payload.url,
        content: payload.url,
        title: `Generated image: ${filename}`,
        type: "image",
      });
    }
  };

  return (
    <>
      <div className="flex justify-start">
        <div className="flex items-center space-x-2 px-3 py-2">
          <Activity className="w-3 h-3 text-purple-500" />
          <span className="text-xs text-gray-500 truncate max-w-md">
            Generated image:{" "}
            <span className="font-bold text-gray-700">
              {truncateFilename(filename)}
            </span>
          </span>
          {(payload.url || payload.content) && (
            <>
              {payload.url && (
                <button
                  onClick={toggleExpanded}
                  className="flex items-center py-0.5 text-xs text-gray-400 hover:text-gray-600 transition-colors"
                  title={isExpanded ? "Hide preview" : "Show preview"}
                >
                  <div
                    className={`transition-transform duration-200 ${
                      isExpanded ? "rotate-90" : "rotate-0"
                    }`}
                  >
                    <ChevronRight className="w-3 h-3" />
                  </div>
                </button>
              )}
              <button
                onClick={handlePreviewClick}
                className="flex items-center ml-2 px-1 py-0.5 text-xs text-gray-400 hover:text-gray-600 transition-colors"
                title="View in preview"
              >
                <Eye className="w-3 h-3" />
              </button>
            </>
          )}
        </div>
      </div>

      {payload.url && (
        <div
          className={`grid transition-all duration-300 ease-in-out ${
            isExpanded
              ? "grid-rows-[1fr] opacity-100"
              : "grid-rows-[0fr] opacity-0"
          }`}
        >
          <div className="overflow-hidden">
            <div className="mx-3 mb-4 bg-gray-50 border border-gray-200 rounded-md overflow-hidden">
              <div className="flex items-center px-3 py-2 bg-gray-100 border-b border-gray-200">
                <Activity className="w-4 h-4 mr-2 text-purple-500" />
                <span className="text-sm font-mono text-gray-700">
                  {filename}
                </span>
              </div>
              <div className="p-3">
                <img
                  src={payload.url}
                  alt={`Generated image: ${filename}`}
                  className="max-w-full h-auto rounded border"
                  loading="lazy"
                />
                {payload.prompt && (
                  <div className="mt-3 pt-3 border-t border-gray-200">
                    <p className="text-xs text-gray-600 font-medium mb-1">
                      Prompt:
                    </p>
                    <div className="text-xs text-gray-700 bg-white p-2 rounded border">
                      {payload.prompt}
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default ImageGenerationEvent;
