import React from "react";
import { Globe, Activity } from "lucide-react";
import MarkdownRenderer from "../MarkdownRenderer";

const WebSearchEvent = ({ payload, eventType }) => {
  if (!payload) return null;

  // Determine title and icon based on event type
  const getEventInfo = (eventType) => {
    switch (eventType) {
      case "web_search":
        return {
          icon: <Globe className="w-4 h-4 text-orange-500" />,
          color: "bg-orange-50 border-orange-200",
          title: "Web Search",
        };
      case "web_search_result":
        return {
          icon: <Activity className="w-4 h-4 text-orange-500" />,
          color: "bg-orange-50 border-orange-300",
          title: "Web Search Result",
        };
      default:
        return {
          icon: <Globe className="w-4 h-4 text-orange-500" />,
          color: "bg-orange-50 border-orange-200",
          title: "Web Operation",
        };
    }
  };

  const eventInfo = getEventInfo(eventType);
  let innerContent = null;

  // Handle list payloads (search results)
  if (Array.isArray(payload)) {
    if (eventType === "web_search_result") {
      innerContent = (
        <div className="mt-2">
          <p className="text-sm text-gray-700 font-medium">Search Results:</p>
          <div className="mt-1 space-y-1">
            {payload.slice(0, 5).map((result, index) => (
              <div
                key={index}
                className="text-sm text-gray-600 bg-gray-50 p-2 rounded"
              >
                <a
                  href={result.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 hover:underline font-medium"
                >
                  {result.title}
                </a>
                <p className="text-xs text-gray-500 mt-1">{result.url}</p>
              </div>
            ))}
            {payload.length > 5 && (
              <p className="text-xs text-gray-500 mt-1">
                ... and {payload.length - 5} more results
              </p>
            )}
          </div>
        </div>
      );
    }
  }

  // Handle search query
  if (eventType === "web_search") {
    innerContent = (
      <div className="mt-2">
        <p className="text-sm text-gray-700 font-medium">Search Query:</p>
        <div className="mt-1">
          <MarkdownRenderer>{payload.query}</MarkdownRenderer>
        </div>
      </div>
    );
  }

  if (!innerContent) return null;

  const content = (
    <div>
      <div className="flex items-center space-x-2 mb-2">
        {eventInfo.icon}
        <span className="font-medium text-sm text-gray-900">
          {eventInfo.title}
        </span>
      </div>
      {innerContent}
    </div>
  );

  return {
    color: eventInfo.color,
    content,
  };
};

export default WebSearchEvent;
