import React, { useState, useRef, useEffect } from "react";
import { Send } from "lucide-react";
import EventCard from "./components/EventCard";
import MessageCard from "./components/MessageCard";
import ContentSidebar from "./components/ContentSidebar";
import "./App.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [isConnected, setIsConnected] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [previewData, setPreviewData] = useState(null);
  const messagesEndRef = useRef(null);
  const pandaRef = useRef(null);

  // Advanced animation state
  const animationRef = useRef({
    mouseX: 0,
    mouseY: 0,
    currentX: 0,
    currentY: 0,
    velocityX: 0,
    velocityY: 0,
    rotation: 0,
    scale: 1,
    floatOffset: 0,
  });

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Advanced mouse tracking with spring physics
  useEffect(() => {
    let animationId;

    const handleMouseMove = (e) => {
      animationRef.current.mouseX = e.clientX;
      animationRef.current.mouseY = e.clientY;
    };

    const animate = () => {
      if (!pandaRef.current) {
        animationId = requestAnimationFrame(animate);
        return;
      }

      const rect = pandaRef.current.getBoundingClientRect();
      const pandaCenterX = rect.left + rect.width / 2;
      const pandaCenterY = rect.top + rect.height / 2;

      // Calculate target position (more responsive)
      const targetX = (animationRef.current.mouseX - pandaCenterX) * 0.15;
      const targetY = (animationRef.current.mouseY - pandaCenterY) * 0.15;

      // Limit movement range
      const maxMove = 8;
      const clampedTargetX = Math.max(-maxMove, Math.min(maxMove, targetX));
      const clampedTargetY = Math.max(-maxMove, Math.min(maxMove, targetY));

      // Spring physics
      const spring = 0.15;
      const friction = 0.8;

      animationRef.current.velocityX +=
        (clampedTargetX - animationRef.current.currentX) * spring;
      animationRef.current.velocityY +=
        (clampedTargetY - animationRef.current.currentY) * spring;

      animationRef.current.velocityX *= friction;
      animationRef.current.velocityY *= friction;

      animationRef.current.currentX += animationRef.current.velocityX;
      animationRef.current.currentY += animationRef.current.velocityY;

      // Calculate rotation based on movement direction
      const rotationTarget = animationRef.current.velocityX * 2;
      animationRef.current.rotation +=
        (rotationTarget - animationRef.current.rotation) * 0.1;

      // Floating animation
      animationRef.current.floatOffset += 0.05;
      const floatY = Math.sin(animationRef.current.floatOffset) * 1.5;

      // Distance-based scale effect
      const distance = Math.sqrt(targetX * targetX + targetY * targetY);
      const maxDistance = 100;
      const scaleTarget = 1 + Math.min(distance / maxDistance, 1) * 0.2;
      animationRef.current.scale +=
        (scaleTarget - animationRef.current.scale) * 0.1;

      // Apply transformations
      if (pandaRef.current) {
        pandaRef.current.style.transform = `
          translate(${animationRef.current.currentX}px, ${
          animationRef.current.currentY + floatY
        }px) 
          rotate(${animationRef.current.rotation}deg) 
          scale(${animationRef.current.scale})
        `;
      }

      animationId = requestAnimationFrame(animate);
    };

    window.addEventListener("mousemove", handleMouseMove);
    animationId = requestAnimationFrame(animate);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
      cancelAnimationFrame(animationId);
    };
  }, []);

  const handlePreviewClick = (data) => {
    setPreviewData(data);
    setSidebarOpen(true);
  };

  const closeSidebar = () => {
    setSidebarOpen(false);
    setPreviewData(null);
  };

  const sendMessage = async () => {
    if (!inputValue.trim()) return;

    const userMessage = {
      id: Date.now(),
      type: "user",
      content: inputValue,
      timestamp: new Date().toISOString(),
    };

    setMessages((prev) => [...prev, userMessage]);
    setInputValue("");
    setIsLoading(true);

    try {
      const apiUrl =
        process.env.NODE_ENV === "production"
          ? "/api/agent/run"
          : "http://localhost:8001/agent/run";
      const response = await fetch(apiUrl, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          query: inputValue,
        }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      setIsConnected(true);

      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value);
        const lines = chunk.split("\n");

        for (const line of lines) {
          if (line.startsWith("data: ")) {
            try {
              const eventData = JSON.parse(line.slice(6));

              // Validate that eventData has the expected structure
              if (eventData && typeof eventData === "object") {
                // Check for token/credit errors in user_notification events
                if (
                  eventData.data &&
                  eventData.data.type === "user_notification" &&
                  eventData.data.payload &&
                  eventData.data.payload.error &&
                  (eventData.data.payload.error.includes("token") ||
                    eventData.data.payload.error.includes("credit"))
                ) {
                  // Add visual indicator to show this is an important error
                  setIsLoading(false);
                }

                const eventMessage = {
                  id: Date.now() + Math.random(),
                  type: "event",
                  event: eventData,
                  timestamp: new Date().toISOString(),
                };
                setMessages((prev) => [...prev, eventMessage]);
              } else {
                console.warn("Received malformed event data:", eventData);
              }
            } catch (e) {
              console.error("Error parsing event data:", e, "Line:", line);
            }
          }
        }
      }
    } catch (error) {
      console.error("Error:", error);
      const errorMessage = {
        id: Date.now(),
        type: "error",
        content: `Connection error: ${error.message}`,
        timestamp: new Date().toISOString(),
      };
      setMessages((prev) => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
      setIsConnected(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="flex h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      {/* Main content */}
      <div
        className={`flex flex-col transition-all duration-300 ${
          sidebarOpen ? "w-[calc(100%-24rem)]" : "w-full"
        }`}
      >
        {/* Header */}
        <div className="bg-white shadow-sm border-b border-gray-200 p-4">
          <div className="max-w-4xl mx-auto flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div
                ref={pandaRef}
                className="w-10 h-10 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-full flex items-center justify-center transition-all duration-200 ease-out hover:shadow-lg cursor-pointer"
                style={{
                  transformOrigin: "center",
                  willChange: "transform",
                }}
              >
                <span className="text-2xl select-none">üêº</span>
              </div>
              <div>
                <h1 className="text-xl font-semibold text-gray-900">
                  PandaAGI Assistant
                </h1>
                <p className="text-sm text-gray-500">
                  {isConnected ? (
                    <span className="flex items-center">
                      <div className="w-2 h-2 bg-green-500 rounded-full mr-2 animate-pulse"></div>
                      Connected
                    </span>
                  ) : (
                    "Ready to help"
                  )}
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Messages */}
        <div className="flex-1 overflow-y-auto p-4 scrollbar-hide">
          <div className="max-w-4xl mx-auto space-y-4">
            {messages.length === 0 && (
              <div className="text-center py-12">
                <div className="text-6xl mb-4 transition-transform duration-300 hover:scale-110 animate-bounce">
                  üêº
                </div>
                <h3 className="text-lg font-medium text-gray-900 mb-2">
                  Welcome to PandaAGI
                </h3>
                <p className="text-gray-500">
                  Start a conversation to see real-time agent events
                </p>
              </div>
            )}

            {messages.map((message) => (
              <div key={message.id} className="animate-slide-up">
                {(message.type === "user" || message.type === "error") && (
                  <MessageCard message={message} />
                )}

                {message.type === "event" && (
                  <EventCard
                    message={message}
                    onPreviewClick={handlePreviewClick}
                  />
                )}
              </div>
            ))}

            {isLoading && (
              <div className="flex justify-start mb-4">
                <div className="flex items-center space-x-2 px-3 py-2">
                  <div className="flex items-center space-x-1">
                    <div className="w-1.5 h-1.5 bg-gray-400 rounded-full animate-pulse"></div>
                    <div className="w-1.5 h-1.5 bg-gray-400 rounded-full animate-pulse [animation-delay:0.2s]"></div>
                    <div className="w-1.5 h-1.5 bg-gray-400 rounded-full animate-pulse [animation-delay:0.4s]"></div>
                  </div>
                  <span className="text-xs text-gray-500">
                    Panda is thinking
                  </span>
                </div>
              </div>
            )}

            <div ref={messagesEndRef} />
          </div>
        </div>

        {/* Input */}
        <div className="bg-white border-t border-gray-200 p-4">
          <div className="max-w-4xl mx-auto">
            <div className="flex space-x-4">
              <div className="flex-1 relative">
                <textarea
                  value={inputValue}
                  onChange={(e) => setInputValue(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Ask the panda anything..."
                  className="w-full p-3 pr-12 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                  rows="1"
                  disabled={isLoading}
                />
                <button
                  onClick={sendMessage}
                  disabled={!inputValue.trim() || isLoading}
                  className="absolute right-2 top-2 p-2 text-blue-500 hover:text-blue-600 disabled:text-gray-400 disabled:cursor-not-allowed"
                >
                  <Send className="w-5 h-5" />
                </button>
              </div>
            </div>
            <p className="text-xs text-gray-500 mt-2">
              Press Enter to send, Shift+Enter for new line
            </p>
          </div>
        </div>
      </div>

      {/* Sidebar */}
      <ContentSidebar
        isOpen={sidebarOpen}
        onClose={closeSidebar}
        previewData={previewData}
      />
    </div>
  );
}

export default App;
