import React, { useState } from "react";
import {
  X,
  ExternalLink,
  FileCode,
  FileText,
  FileImage,
  File,
  Code,
  Eye,
} from "lucide-react";
import MarkdownRenderer from "./MarkdownRenderer";

const ContentSidebar = ({ isOpen, onClose, previewData }) => {
  const [htmlViewMode, setHtmlViewMode] = useState("preview"); // "code" or "preview"

  if (!isOpen || !previewData) return null;

  // Get syntax highlighting class based on file extension
  const getSyntaxClass = (filename) => {
    if (!filename) return "";
    const extension = filename.split(".").pop().toLowerCase();

    const syntaxMap = {
      js: "language-javascript",
      jsx: "language-jsx",
      ts: "language-typescript",
      tsx: "language-tsx",
      py: "language-python",
      css: "language-css",
      scss: "language-scss",
      html: "language-html",
      json: "language-json",
      xml: "language-xml",
      yaml: "language-yaml",
      yml: "language-yaml",
    };

    return syntaxMap[extension] || "";
  };

  // Render content based on type
  const renderContent = () => {
    const type = previewData.type || "text";
    const content = previewData.content || "";

    switch (type) {
      case "markdown":
        return (
          <div className="prose prose-sm max-w-none">
            <MarkdownRenderer>{content}</MarkdownRenderer>
          </div>
        );
      case "html":
        return (
          <div>
            {/* Toggle buttons */}
            <div className="flex space-x-2 mb-4 border-b pb-2">
              <button
                onClick={() => setHtmlViewMode("preview")}
                className={`flex items-center space-x-1 px-3 py-1 rounded text-sm transition-colors ${
                  htmlViewMode === "preview"
                    ? "bg-blue-100 text-blue-700"
                    : "bg-gray-100 text-gray-600 hover:bg-gray-200"
                }`}
              >
                <Eye className="w-3 h-3" />
                <span>Preview</span>
              </button>
              <button
                onClick={() => setHtmlViewMode("code")}
                className={`flex items-center space-x-1 px-3 py-1 rounded text-sm transition-colors ${
                  htmlViewMode === "code"
                    ? "bg-blue-100 text-blue-700"
                    : "bg-gray-100 text-gray-600 hover:bg-gray-200"
                }`}
              >
                <Code className="w-3 h-3" />
                <span>Code</span>
              </button>
            </div>

            {htmlViewMode === "preview" ? (
              <div className="border rounded bg-white">
                <iframe
                  srcDoc={content}
                  className="w-full h-[60vh] border-0 rounded"
                  title="HTML Preview"
                  sandbox="allow-scripts allow-same-origin"
                />
              </div>
            ) : (
              <div className="editor-container bg-gray-900 text-gray-100 p-4 rounded overflow-x-auto">
                <div className="flex items-center justify-between mb-2 text-xs text-gray-400">
                  <span>HTML</span>
                  <span>{content.split("\n").length} lines</span>
                </div>
                <pre
                  className={`whitespace-pre-wrap text-sm ${getSyntaxClass(
                    previewData.url
                  )}`}
                >
                  <code>{content}</code>
                </pre>
              </div>
            )}
          </div>
        );
      case "image":
        // For images, the content could be a data URL, file path, or base64
        if (
          content.startsWith("data:image") ||
          content.startsWith("http") ||
          content.includes("base64")
        ) {
          return (
            <div className="flex justify-center">
              <img
                src={content}
                alt="Preview"
                className="max-w-full max-h-[80vh] object-contain rounded border"
                onError={(e) => {
                  e.target.style.display = "none";
                  e.target.nextSibling.style.display = "block";
                }}
              />
              <div className="hidden text-center text-gray-500 p-4">
                <FileImage className="w-12 h-12 mx-auto mb-2 text-gray-400" />
                <p>Image preview not available</p>
                <p className="text-xs text-gray-400 mt-1">
                  File: {previewData.url}
                </p>
              </div>
            </div>
          );
        } else {
          return (
            <div className="text-center text-gray-500 p-4">
              <FileImage className="w-12 h-12 mx-auto mb-2 text-gray-400" />
              <p>Image file detected</p>
              <p className="text-xs text-gray-400 mt-1">
                Content preview not available
              </p>
              <div className="mt-4 editor-container bg-gray-900 text-gray-100 p-4 rounded overflow-x-auto">
                <pre className="whitespace-pre-wrap text-sm">{content}</pre>
              </div>
            </div>
          );
        }
      case "pdf":
        return (
          <div className="text-center text-gray-500 p-4">
            <File className="w-12 h-12 mx-auto mb-2 text-red-500" />
            <p>PDF file detected</p>
            <p className="text-xs text-gray-400 mt-1">
              PDF preview not available in browser
            </p>
            {previewData.url && (
              <a
                href={previewData.url}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block mt-3 px-4 py-2 bg-red-100 text-red-700 rounded hover:bg-red-200 transition-colors"
              >
                Open PDF in new tab
              </a>
            )}
            {content && (
              <div className="mt-4 editor-container bg-gray-900 text-gray-100 p-4 rounded overflow-x-auto">
                <div className="flex items-center justify-between mb-2 text-xs text-gray-400">
                  <span>PDF Content (if readable)</span>
                  <span>{content.split("\n").length} lines</span>
                </div>
                <pre className="whitespace-pre-wrap text-sm">{content}</pre>
              </div>
            )}
          </div>
        );
      case "code":
        const filename = previewData.url || "";
        const extension = filename.split(".").pop().toLowerCase();
        const languageMap = {
          js: "JavaScript",
          jsx: "React JSX",
          ts: "TypeScript",
          tsx: "React TSX",
          py: "Python",
          css: "CSS",
          scss: "SCSS",
          json: "JSON",
          xml: "XML",
          yaml: "YAML",
          yml: "YAML",
        };
        const language = languageMap[extension] || extension.toUpperCase();

        return (
          <div className="editor-container bg-gray-900 text-gray-100 rounded overflow-hidden">
            {/* Editor Header */}
            <div className="flex items-center justify-between px-4 py-2 bg-gray-800 border-b border-gray-700">
              <div className="flex items-center space-x-2">
                <div className="flex space-x-1">
                  <div className="w-3 h-3 rounded-full bg-red-500"></div>
                  <div className="w-3 h-3 rounded-full bg-yellow-500"></div>
                  <div className="w-3 h-3 rounded-full bg-green-500"></div>
                </div>
                <span className="text-xs text-gray-300 ml-2">
                  {filename.split("/").pop()}
                </span>
              </div>
              <div className="flex items-center space-x-3 text-xs text-gray-400">
                <span>{language}</span>
                <span>{content.split("\n").length} lines</span>
              </div>
            </div>

            {/* Line numbers and code */}
            <div className="flex">
              <div className="bg-gray-800 px-3 py-4 text-xs text-gray-500 select-none border-r border-gray-700">
                {content.split("\n").map((_, index) => (
                  <div key={index} className="leading-6">
                    {index + 1}
                  </div>
                ))}
              </div>
              <div className="flex-1 p-4 overflow-x-auto">
                <pre
                  className={`text-sm leading-6 ${getSyntaxClass(filename)}`}
                >
                  <code>{content}</code>
                </pre>
              </div>
            </div>
          </div>
        );
      default:
        return (
          <div className="editor-container bg-gray-900 text-gray-100 p-4 rounded overflow-x-auto">
            <div className="flex items-center justify-between mb-2 text-xs text-gray-400">
              <span>Text File</span>
              <span>{content.split("\n").length} lines</span>
            </div>
            <pre className="whitespace-pre-wrap text-sm">{content}</pre>
          </div>
        );
    }
  };

  // Get file type icon
  const getFileIcon = () => {
    const type = previewData.type || "text";

    switch (type) {
      case "code":
        return <FileCode className="w-4 h-4 text-blue-500 mr-1" />;
      case "image":
        return <FileImage className="w-4 h-4 text-green-500 mr-1" />;
      case "pdf":
        return <File className="w-4 h-4 text-red-500 mr-1" />;
      case "markdown":
        return <FileText className="w-4 h-4 text-purple-500 mr-1" />;
      case "html":
        return <FileCode className="w-4 h-4 text-orange-500 mr-1" />;
      default:
        return <FileText className="w-4 h-4 text-gray-500 mr-1" />;
    }
  };

  return (
    <div className="fixed right-0 top-0 h-full w-[55vw] bg-white border-l border-gray-200 shadow-lg z-50 flex flex-col">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-gray-50">
        <div className="flex-1 min-w-0">
          <h3 className="text-sm font-medium text-gray-900 truncate flex items-center">
            {getFileIcon()}
            {previewData.title}
          </h3>
          {previewData.url && (
            <a
              href={previewData.url}
              target="_blank"
              rel="noopener noreferrer"
              className="text-xs text-blue-600 hover:underline flex items-center space-x-1 mt-1"
            >
              <span className="truncate">{previewData.url}</span>
              <ExternalLink className="w-3 h-3 flex-shrink-0" />
            </a>
          )}
        </div>
        <button
          onClick={onClose}
          className="ml-2 p-1 hover:bg-gray-200 rounded transition-colors"
          title="Close preview"
        >
          <X className="w-4 h-4 text-gray-500" />
        </button>
      </div>

      {/* Content */}
      <div className="flex-1 overflow-y-auto p-4">{renderContent()}</div>
    </div>
  );
};

export default ContentSidebar;
