import React from "react";
import {
  AlertCircle,
  CreditCard,
  FileText,
  Image,
  File,
  Code,
  Archive,
  Download,
  Eye,
} from "lucide-react";
import MarkdownRenderer from "../MarkdownRenderer";

const UserNotificationEvent = ({
  payload,
  eventType,
  onPreviewClick,
  onFileClick,
  timestamp,
}) => {
  if (!payload) return null;

  const formatTimestamp = (timestamp) => {
    const now = new Date();
    const eventTime = new Date(timestamp);
    const diffInSeconds = Math.floor((now - eventTime) / 1000);

    if (diffInSeconds < 60) {
      return diffInSeconds <= 1 ? "just now" : `${diffInSeconds}s ago`;
    }

    const diffInMinutes = Math.floor(diffInSeconds / 60);
    if (diffInMinutes < 60) {
      return diffInMinutes === 1 ? "1 min ago" : `${diffInMinutes} mins ago`;
    }

    const diffInHours = Math.floor(diffInMinutes / 60);
    if (diffInHours < 24) {
      return diffInHours === 1 ? "1 hour ago" : `${diffInHours} hours ago`;
    }

    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) {
      return diffInDays === 1 ? "1 day ago" : `${diffInDays} days ago`;
    }

    // For older events, fall back to date format
    return eventTime.toLocaleDateString();
  };

  // Check if this is an error notification
  const isError =
    payload.error ||
    (payload.text && payload.text.toLowerCase().includes("error"));

  // Check specifically for token/credit related errors
  const isTokenError =
    isError &&
    ((payload.error && payload.error.toLowerCase().includes("token")) ||
      (payload.error && payload.error.toLowerCase().includes("credit")) ||
      typeof payload.credits_left !== "undefined");

  const getFileIcon = (filename) => {
    if (!filename) return <File className="w-5 h-5 text-gray-500" />;

    const extension = filename.split(".").pop()?.toLowerCase();
    const iconClass = "w-5 h-5";

    if (
      ["jpg", "jpeg", "png", "gif", "svg", "webp", "bmp"].includes(extension)
    ) {
      return <Image className={`${iconClass} text-green-500`} />;
    }
    if (
      [
        "js",
        "jsx",
        "ts",
        "tsx",
        "py",
        "java",
        "c",
        "cpp",
        "go",
        "rb",
        "php",
        "css",
        "scss",
        "json",
        "xml",
        "html",
        "htm",
      ].includes(extension)
    ) {
      return <Code className={`${iconClass} text-blue-500`} />;
    }
    if (
      ["md", "txt", "doc", "docx", "pdf", "csv", "xls", "xlsx"].includes(
        extension
      )
    ) {
      return <FileText className={`${iconClass} text-orange-500`} />;
    }
    if (["zip", "rar", "7z", "tar", "gz"].includes(extension)) {
      return <Archive className={`${iconClass} text-purple-500`} />;
    }
    return <File className={`${iconClass} text-gray-500`} />;
  };

  const getFileTypeLabel = (filename) => {
    if (!filename) return "File";

    const extension = filename.split(".").pop()?.toLowerCase();

    if (
      ["jpg", "jpeg", "png", "gif", "svg", "webp", "bmp"].includes(extension)
    ) {
      return "Image";
    }
    if (
      [
        "js",
        "jsx",
        "ts",
        "tsx",
        "py",
        "java",
        "c",
        "cpp",
        "go",
        "rb",
        "php",
      ].includes(extension)
    ) {
      return "Code";
    }
    if (["css", "scss", "html", "htm"].includes(extension)) {
      return "Web";
    }
    if (["json", "xml", "yaml", "yml"].includes(extension)) {
      return "Data";
    }
    if (["md", "txt"].includes(extension)) {
      return "Text";
    }
    if (["doc", "docx", "pdf"].includes(extension)) {
      return "Document";
    }
    if (["csv", "xls", "xlsx"].includes(extension)) {
      return "Spreadsheet";
    }
    if (["zip", "rar", "7z", "tar", "gz"].includes(extension)) {
      return "Archive";
    }
    return extension ? extension.toUpperCase() : "File";
  };

  const handleFileClick = (filename) => {
    if (onFileClick) {
      onFileClick(filename);
    }
  };

  const renderErrorContent = () => {
    return (
      <div>
        <div className="flex items-start">
          <AlertCircle className="w-5 h-5 text-red-600 mt-0.5 mr-3 flex-shrink-0" />
          <div className="flex-1">
            <h4 className="font-semibold text-gray-900 text-sm">
              {isTokenError ? "Insufficient Credits" : "Error"}
            </h4>
            <p className="text-sm text-gray-700 mt-1 leading-relaxed">
              {payload.error ||
                payload.text ||
                payload.message ||
                "An error occurred"}
            </p>

            {isTokenError && typeof payload.credits_left !== "undefined" && (
              <div className="mt-3 bg-gray-50 border border-gray-200 p-3 rounded-lg">
                <div className="flex items-center justify-between text-sm">
                  <span className="flex items-center text-gray-600 font-medium">
                    <CreditCard className="w-4 h-4 mr-2 text-gray-500" />
                    Credits Remaining
                  </span>
                  <span className="font-semibold text-gray-900">
                    {payload.credits_left} / {payload.total_credits || "âˆž"}
                  </span>
                </div>
              </div>
            )}
          </div>
        </div>
        {timestamp && (
          <p className="text-xs text-gray-400 mt-3 text-right font-medium">
            {formatTimestamp(timestamp)}
          </p>
        )}
      </div>
    );
  };

  const renderStandardContent = () => (
    <div>
      <MarkdownRenderer onPreviewClick={onPreviewClick}>
        {payload.text || payload.message}
      </MarkdownRenderer>
      {timestamp && (
        <p className="text-xs text-gray-400 mt-3 text-right font-medium">
          {formatTimestamp(timestamp)}
        </p>
      )}
    </div>
  );

  const content = isError ? renderErrorContent() : renderStandardContent();

  // Render attachments as beautiful file cards
  const attachments =
    payload.attachments && payload.attachments.length > 0 ? (
      <div className="mt-4 space-y-3">
        <div className="text-xs font-medium text-gray-600 flex items-center">
          <Download className="w-3 h-3 mr-1" />
          {payload.attachments.length} attachment
          {payload.attachments.length !== 1 ? "s" : ""}
        </div>
        <div className="space-y-2">
          {payload.attachments.map((attachment, index) => {
            const filename = attachment.split("/").pop();
            const fileIcon = getFileIcon(filename);
            const fileTypeLabel = getFileTypeLabel(filename);

            return (
              <div key={index} className="flex justify-start">
                <div
                  onClick={() => handleFileClick(attachment)}
                  className="group flex items-center space-x-3 p-3 bg-white hover:bg-gray-50 border border-gray-200 rounded-xl shadow-sm hover:shadow-md transition-all duration-200 cursor-pointer min-w-0 max-w-md"
                >
                  {/* File Icon */}
                  <div className="flex-shrink-0">{fileIcon}</div>

                  {/* File Info */}
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center justify-between">
                      <div className="min-w-0 flex-1">
                        <p className="text-sm font-medium text-gray-900 truncate group-hover:text-gray-700">
                          {filename}
                        </p>
                        <p className="text-xs text-gray-500 mt-0.5">
                          {fileTypeLabel}
                        </p>
                      </div>
                    </div>
                  </div>

                  {/* Action Button */}
                  <div className="flex-shrink-0 opacity-0 group-hover:opacity-100 transition-opacity duration-200">
                    <div className="flex items-center justify-center w-8 h-8 rounded-lg bg-blue-50 text-blue-600 group-hover:bg-blue-100">
                      <Eye className="w-4 h-4" />
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    ) : null;

  return {
    color: isError ? "bg-red-50 border-red-200/60" : "bg-white border-gray-200",
    content,
    attachments,
  };
};

export default UserNotificationEvent;
