import React from "react";
import { X, ExternalLink } from "lucide-react";
import MarkdownRenderer from "./MarkdownRenderer";

const ContentSidebar = ({ isOpen, onClose, previewData }) => {
  if (!isOpen || !previewData) return null;

  return (
    <div className="fixed right-0 top-0 h-full w-96 bg-white border-l border-gray-200 shadow-lg z-50 flex flex-col">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-gray-50">
        <div className="flex-1 min-w-0">
          <h3 className="text-sm font-medium text-gray-900 truncate">
            {previewData.title}
          </h3>
          <a
            href={previewData.url}
            target="_blank"
            rel="noopener noreferrer"
            className="text-xs text-blue-600 hover:underline flex items-center space-x-1 mt-1"
          >
            <span className="truncate">{previewData.url}</span>
            <ExternalLink className="w-3 h-3 flex-shrink-0" />
          </a>
        </div>
        <button
          onClick={onClose}
          className="ml-2 p-1 hover:bg-gray-200 rounded transition-colors"
          title="Close preview"
        >
          <X className="w-4 h-4 text-gray-500" />
        </button>
      </div>

      {/* Content */}
      <div className="flex-1 overflow-y-auto p-4">
        <div className="prose prose-sm max-w-none">
          <MarkdownRenderer>{previewData.content}</MarkdownRenderer>
        </div>
      </div>
    </div>
  );
};

export default ContentSidebar;
