import React from "react";
import { FileText, Eye } from "lucide-react";

const FileOperationEvent = ({ payload, eventType, onPreviewClick }) => {
  if (!payload) return null;

  // Helper function to determine file type based on extension
  const getFileType = (filePath) => {
    if (!filePath) return "text";
    const extension = filePath.split(".").pop().toLowerCase();

    if (["md", "markdown"].includes(extension)) return "markdown";
    if (["html", "htm"].includes(extension)) return "html";
    if (["js", "jsx", "ts", "tsx", "py", "java", "c", "cpp", "go", "rb", "php"].includes(extension)) return "code";
    if (["jpg", "jpeg", "png", "gif", "svg", "webp"].includes(extension)) return "image";
    return "text";
  };

  const getEventInfo = (eventType) => {
    switch (eventType) {
      case "file_read":
        return {
          icon: <FileText className="w-4 h-4 text-cyan-500" />,
          color: "bg-cyan-50 border-cyan-200",
          title: "Reading file",
        };
      case "file_write":
        return {
          icon: <FileText className="w-4 h-4 text-green-600" />,
          color: "bg-green-50 border-green-300",
          title: "Creating file",
        };
      case "file_replace":
        return {
          icon: <FileText className="w-4 h-4 text-yellow-600" />,
          color: "bg-yellow-50 border-yellow-300",
          title: "Editing file",
        };
      case "file_find":
        return {
          icon: <FileText className="w-4 h-4 text-blue-500" />,
          color: "bg-blue-50 border-blue-200",
          title: "Searching for file",
        };
      case "file_explore":
        return {
          icon: <FileText className="w-4 h-4 text-blue-600" />,
          color: "bg-blue-50 border-blue-300",
          title: "Exploring filesystem",
        };
      default:
        return {
          icon: <FileText className="w-4 h-4 text-blue-500" />,
          color: "bg-blue-50 border-blue-200",
          title: "File Operation",
        };
    }
  };

  const eventInfo = getEventInfo(eventType);
  const filePath = payload.file || payload.path;
  const hasContent = Boolean(payload.content);
  const fileType = getFileType(filePath);

  // Handle click to preview content
  const handlePreviewClick = () => {
    if (onPreviewClick && payload.content) {
      onPreviewClick({
        title: `File: ${filePath}`,
        url: filePath,
        content: payload.content,
        type: fileType
      });
    }
  };

  const content = (
    <div>
      <div className="flex items-center space-x-2 mb-2">
        {eventInfo.icon}
        <span className="font-medium text-sm text-gray-900">
          {eventInfo.title}
        </span>
      </div>
      <div className="mt-2">
        <p className="text-sm text-gray-700 font-medium">File:</p>
        <div className="mt-1">
          <code className="text-sm bg-gray-100 px-2 py-1 rounded">
            {filePath}
          </code>
        </div>
        {hasContent && (
          <div className="mt-2">
            <div className="flex items-center justify-between mb-1">
              <p className="text-sm text-gray-700 font-medium">
                Content Preview:
              </p>
              <button
                onClick={handlePreviewClick}
                className="flex items-center space-x-1 px-2 py-1 text-xs text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded transition-colors"
                title="View full content"
              >
                <Eye className="w-3 h-3" />
                <span>View content</span>
              </button>
            </div>
            <div 
              className="mt-1 text-xs text-gray-600 bg-gray-50 p-2 rounded cursor-pointer hover:bg-gray-100 transition-colors"
              onClick={handlePreviewClick}
            >
              {String(payload.content).slice(0, 200)}{payload.content.length > 200 ? "..." : ""}
            </div>
          </div>
        )}
      </div>
    </div>
  );

  return {
    color: eventInfo.color,
    content,
  };
};

export default FileOperationEvent;
