import React from "react";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";

// Function to preprocess text to ensure URLs are detectable by remark-gfm
const preprocessForAutolinks = (text) => {
  if (typeof text !== "string") return text;

  // More comprehensive URL regex that captures the full URL
  const urlRegex = /(https?:\/\/[^\s<>"']+)/gi;

  // Find all URLs and ensure they have proper spacing around them
  return text.replace(
    /(\S)(https?:\/\/[^\s<>"']+)(\S)/g,
    (match, before, url, after) => {
      // Only add spaces if the characters before/after are not already spaces or punctuation
      const needsSpaceBefore = before && !/[\s\(\[\{]/.test(before);
      const needsSpaceAfter = after && !/[\s\)\]\}\.,;!?]/.test(after);

      return (
        (needsSpaceBefore ? before + " " : before) +
        url +
        (needsSpaceAfter ? " " + after : after)
      );
    }
  );
};

// Reusable markdown renderer component
const MarkdownRenderer = ({ children, className = "" }) => {
  // Preprocess the content to ensure URLs are properly spaced
  const processedContent = preprocessForAutolinks(children);

  return (
    <div
      className={`text-sm rounded prose prose-sm prose-gray max-w-none ${className}`}
    >
      <ReactMarkdown
        remarkPlugins={[remarkGfm]}
        components={{
          p: ({ children }) => <p className="mb-2 last:mb-0">{children}</p>,
          h1: ({ children }) => (
            <h1 className="text-lg font-bold mb-2">{children}</h1>
          ),
          h2: ({ children }) => (
            <h2 className="text-base font-bold mb-2">{children}</h2>
          ),
          h3: ({ children }) => (
            <h3 className="text-sm font-bold mb-1">{children}</h3>
          ),
          code: ({ children }) => (
            <code className="bg-gray-200 px-1 py-0.5 rounded text-xs font-mono">
              {children}
            </code>
          ),
          pre: ({ children }) => (
            <pre className="bg-gray-200 p-2 rounded text-xs overflow-x-auto">
              {children}
            </pre>
          ),
          ul: ({ children }) => (
            <ul className="list-disc list-inside mb-2">{children}</ul>
          ),
          ol: ({ children }) => (
            <ol className="list-decimal list-inside mb-2">{children}</ol>
          ),
          li: ({ children }) => <li className="mb-1">{children}</li>,
          blockquote: ({ children }) => (
            <blockquote className="border-l-4 border-gray-300 pl-3 italic">
              {children}
            </blockquote>
          ),
          // Style all links consistently
          a: ({ href, children }) => (
            <a
              href={href}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-600 hover:underline"
            >
              {children}
            </a>
          ),
        }}
      >
        {processedContent}
      </ReactMarkdown>
    </div>
  );
};

export default MarkdownRenderer;
