import React from "react";
import { getEventIcon } from "./utils/eventIcons";
import { getEventColor } from "./utils/eventColors";
import UserNotificationEvent from "./events/UserNotificationEvent";
import WebSearchEvent from "./events/WebSearchEvent";
import WebNavigationEvent from "./events/WebNavigationEvent";
import FileOperationEvent from "./events/FileOperationEvent";
import ShellOperationEvent from "./events/ShellOperationEvent";
import ImageGenerationEvent from "./events/ImageGenerationEvent";
import LegacyEvent from "./events/LegacyEvent";
import UnknownEvent from "./events/UnknownEvent";

const EventCard = ({ message }) => {
  if (!message.event || !message.event.data) return null;

  const eventData = message.event.data;
  const eventType = eventData.type || "unknown";

  const formatTimestamp = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString();
  };

  const renderEventPayload = (payload, eventType) => {
    switch (eventType) {
      // User events
      case "user_notification":
      case "user_question":
        return <UserNotificationEvent payload={payload} />;

      // Web operations
      case "web_search":
      case "web_search_result":
        return <WebSearchEvent payload={payload} eventType={eventType} />;

      case "web_navigation":
      case "web_navigation_result":
        return <WebNavigationEvent payload={payload} />;

      // File operations
      case "file_read":
      case "file_write":
      case "file_replace":
      case "file_find":
      case "file_explore":
        return <FileOperationEvent payload={payload} />;

      // Shell operations
      case "shell_exec":
      case "shell_view":
      case "shell_write":
        return <ShellOperationEvent payload={payload} />;

      // Image operations
      case "image_generation":
        return <ImageGenerationEvent payload={payload} />;

      // Legacy event types
      case "agent_request":
      case "tool_result":
      case "completion":
      case "connection_success":
      case "web_visit_page":
      case "user_send_message":
      case "shell_exec_command":
      case "error":
        return <LegacyEvent payload={payload} eventType={eventType} />;

      // Unknown/fallback
      default:
        return <UnknownEvent payload={payload} eventType={eventType} />;
    }
  };

  return (
    <div className="flex justify-start">
      <div className={`event-card max-w-2xl ${getEventColor(eventType)}`}>
        <div className="flex items-start justify-between mb-2">
          <div className="flex items-center space-x-2">
            {getEventIcon(eventType)}
            <span className="font-medium text-sm text-gray-900">
              {eventType
                .replace(/_/g, " ")
                .replace(/\b\w/g, (l) => l.toUpperCase())}
            </span>
          </div>
          <span className="text-xs text-gray-500">
            {formatTimestamp(eventData.timestamp || message.timestamp)}
          </span>
        </div>

        {eventData.id && (
          <p className="text-xs text-gray-400 font-mono mb-2">
            ID: {eventData.id}
          </p>
        )}

        {renderEventPayload(eventData.payload, eventType)}

        {eventData.status && (
          <div className="mt-2">
            <span
              className={`event-badge ${
                eventData.status === "success"
                  ? "bg-green-100 text-green-800"
                  : "bg-red-100 text-red-800"
              }`}
            >
              {eventData.status}
            </span>
          </div>
        )}
      </div>
    </div>
  );
};

export default EventCard;
