import React from "react";
import { Activity } from "lucide-react";

const ShellOperationEvent = ({ payload, eventType }) => {
  if (!payload) return null;

  const content = (
    <div className="mt-2">
      <p className="text-sm text-gray-700 font-medium">Command:</p>
      <div className="mt-1">
        <code className="text-sm bg-gray-100 px-2 py-1 rounded">
          {payload.command}
        </code>
      </div>
      {payload.id && (
        <div className="mt-1">
          <p className="text-xs text-gray-500">Session: {payload.id}</p>
        </div>
      )}
      {payload.output && (
        <div className="mt-2">
          <p className="text-sm text-gray-700 font-medium">Output:</p>
          <div className="mt-1 text-xs text-gray-600 bg-gray-50 p-2 rounded font-mono">
            {String(payload.output).slice(0, 300)}...
          </div>
        </div>
      )}
    </div>
  );

  const getEventDetails = (eventType) => {
    switch (eventType) {
      case "shell_exec":
        return {
          icon: <Activity className="w-4 h-4 text-purple-600" />,
          color: "bg-purple-50 border-purple-300",
          title: "Shell Execute",
        };
      case "shell_view":
        return {
          icon: <Activity className="w-4 h-4 text-gray-600" />,
          color: "bg-gray-50 border-gray-300",
          title: "Shell View",
        };
      case "shell_write":
        return {
          icon: <Activity className="w-4 h-4 text-yellow-600" />,
          color: "bg-yellow-50 border-yellow-300",
          title: "Shell Write",
        };
      default:
        return {
          icon: <Activity className="w-4 h-4 text-purple-600" />,
          color: "bg-purple-50 border-purple-300",
          title: "Shell Operation",
        };
    }
  };

  const details = getEventDetails(eventType);

  return {
    icon: details.icon,
    color: details.color,
    title: details.title,
    content,
  };
};

export default ShellOperationEvent;
