import React from "react";
import { MessageSquare, AlertCircle, CreditCard } from "lucide-react";
import MarkdownRenderer from "../MarkdownRenderer";

const UserNotificationEvent = ({ payload, eventType, onPreviewClick }) => {
  if (!payload) return null;

  // Check if this is an error notification
  const isError =
    payload.error ||
    (payload.text && payload.text.toLowerCase().includes("error"));

  // Check specifically for token/credit related errors
  const isTokenError =
    isError &&
    ((payload.error && payload.error.toLowerCase().includes("token")) ||
      (payload.error && payload.error.toLowerCase().includes("credit")) ||
      typeof payload.credits_left !== "undefined");

  const renderErrorContent = () => {
    return (
      <div className="bg-red-50 border border-red-200 rounded-md p-3">
        <div className="flex items-start">
          <AlertCircle className="w-5 h-5 text-red-500 mt-0.5 mr-2 flex-shrink-0" />
          <div>
            <h4 className="font-medium text-red-800">
              {isTokenError ? "Insufficient Credits" : "Error"}
            </h4>
            <p className="text-sm text-red-700 mt-1">
              {payload.error ||
                payload.text ||
                payload.message ||
                "An error occurred"}
            </p>

            {isTokenError && typeof payload.credits_left !== "undefined" && (
              <div className="mt-3 bg-white p-2 rounded border border-red-100">
                <div className="flex items-center justify-between text-sm">
                  <span className="flex items-center text-gray-700">
                    <CreditCard className="w-4 h-4 mr-1.5" />
                    Credits Remaining:
                  </span>
                  <span className="font-medium">
                    {payload.credits_left} / {payload.total_credits || "âˆž"}
                  </span>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    );
  };

  const renderStandardContent = () => (
    <div>
      <div className="flex items-center space-x-2 mb-2">
        <MessageSquare className="w-4 h-4 text-indigo-500" />
        <span className="font-medium text-sm text-gray-900">PandaAGI</span>
      </div>
      <div className="mt-2">
        <MarkdownRenderer onPreviewClick={onPreviewClick}>
          {payload.text || payload.message}
        </MarkdownRenderer>
        {payload.attachments && payload.attachments.length > 0 && (
          <div className="mt-4">
            <p className="text-sm text-gray-700 font-medium">Attachments:</p>
            <div className="mt-1 space-y-1">
              {payload.attachments.map((attachment, index) => (
                <div key={index} className="text-sm p-1 rounded">
                  ðŸ“Ž {attachment}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );

  const content = isError ? renderErrorContent() : renderStandardContent();

  return {
    color: isError
      ? "bg-red-50 border-red-200"
      : "bg-indigo-50 border-indigo-200",
    content,
  };
};

export default UserNotificationEvent;
