import React, { useState, useCallback } from "react";
import { Activity, Eye } from "lucide-react";

const ImageGenerationEvent = ({ payload, onPreviewClick }) => {
  const [isLoading, setIsLoading] = useState(false);

  const readFileContent = useCallback(async (filePath) => {
    try {
      setIsLoading(true);
      const response = await fetch(
        `/api/files/read?path=${encodeURIComponent(filePath)}`
      );
      if (!response.ok) {
        throw new Error(`Failed to read file: ${response.statusText}`);
      }
      const blob = await response.blob();
      return URL.createObjectURL(blob);
    } catch (error) {
      console.error("Error reading file:", error);
      // Fallback to direct file path
      return filePath;
    } finally {
      setIsLoading(false);
    }
  }, []);

  if (!payload) return null;

  const filename = payload.filename || "generated_image.png";

  const truncateFilename = (filename, maxLength = 50) => {
    if (!filename) return "Unknown file";
    return filename.length > maxLength
      ? `${filename.substring(0, maxLength)}...`
      : filename;
  };

  const handlePreviewClick = async () => {
    if (
      payload["saved_files"] &&
      payload["saved_files"].length > 0 &&
      onPreviewClick
    ) {
      const filePath = payload["saved_files"][0];
      const content = await readFileContent(filePath);

      onPreviewClick({
        url: content,
        content: content,
        title: `Generated image: ${filename}`,
        type: "image",
      });
    }
  };

  return (
    <div className="flex justify-start">
      <div className="flex items-center space-x-2 px-3 py-2">
        <Activity className="w-3 h-3 text-purple-500" />
        <span className="text-xs text-gray-500 truncate max-w-md">
          Generated image:{" "}
          <span className="font-bold text-gray-700">
            {truncateFilename(filename)}
          </span>
        </span>
        {payload["saved_files"] && payload["saved_files"].length > 0 && (
          <button
            onClick={handlePreviewClick}
            disabled={isLoading}
            className={`flex items-center ml-2 px-1 py-0.5 text-xs transition-colors ${
              isLoading
                ? "text-gray-300 cursor-not-allowed"
                : "text-gray-400 hover:text-gray-600"
            }`}
            title={isLoading ? "Loading..." : "View in preview"}
          >
            <Eye className="w-3 h-3" />
          </button>
        )}
      </div>
    </div>
  );
};

export default ImageGenerationEvent;
